import React from 'react';
import './leftColumn.css';
import MainPost from '../utilities/mainPost/mainPost';

const leftColumn = (props) => {

    const posts = [...props.posts];
    const currentPage = props.currentPage;
    const pageCount = Math.floor(currentPage / 3);

    return ( 
        <React.Fragment>
            <div className={'LeftColumn'}>
            {posts.map(post => <MainPost content={post} />)}
            </div>
            <div className="leftColumnPagination">
                <ul>
                    <li className={'paginationItem'}>Previous</li>
                    <li className={'paginationItem'}>0</li>
                    {posts.map((index) => {
                        if(index % 3 === 0) {
                            <li className={'paginationItem'}>{index/3 + 1}</li>
                        }
                    })}
                    <li className={'paginationItem'}>Next</li>
                </ul>
            </div>
        </React.Fragment>
     );
}

export default leftColumn;
