import React from 'react';
import './footer.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMapMarker } from '@fortawesome/free-solid-svg-icons'
import { faInstagram, faTwitter, faPinterest } from '@fortawesome/free-brands-svg-icons';
import FooterText from '../utilities/footerText/footerText';

const footer = (props) => {

    const posts = [...props.posts].slice(0, 3);
    const images = [...props.posts];
    const selectedImages = [];

    const random1 = Math.floor(Math.random() * images.length);
    const image1 = images[random1].image;
    images.slice(random1, 1);
    const random2 = Math.floor(Math.random() * images.length);
    const image2 = images[random2].image;
    images.slice(random2, 1);
    const random3 = Math.floor(Math.random() * images.length);
    const image3 = images[random3].image;
    images.slice(random3, 1);
    const random4 = Math.floor(Math.random() * images.length);
    const image4 = images[random4].image;
    images.slice(random4, 1);
    const random5 = Math.floor(Math.random() * images.length);
    const image5 = images[random5].image;
    images.slice(random5, 1);
    const random6 = Math.floor(Math.random() * images.length);
    const image6 = images[random6].image;
    images.slice(random6, 1);

    for(var i = 0; i < 7; i++) {
        let random = Math.floor(Math.random() * images.length);
        selectedImages.push(images[random1].image);
        images.slice(random, 1);
        console.log(random, images.length)
    }

    return ( 
        <div className={'footer'}>
            <div className="footerText">
                <div className="logo footerLogo"><FontAwesomeIcon icon={faMapMarker} /><p>My Next Trip !</p></div><br />
                <p className={'footerP'}>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Tempora, maxime? Quia necessitatibus modi placeat repellendus dolorum tempore voluptatibus? Nisi est.</p>
                <div className="socialLinks footerSocialLinks">
                    <FontAwesomeIcon icon={faInstagram} className={'fa-2x'} style={{margin:'0 15px 0', color:'#628A76'}} />
                    <FontAwesomeIcon icon={faTwitter} className={'fa-2x'} style={{margin:'0 15px 0', color:'#628A76'}} />
                    <FontAwesomeIcon icon={faPinterest} className={'fa-2x'} style={{margin:'0 15px 0', color:'#628A76'}} />
                </div>
            </div>
            <div className="footerNews">
                <h5 className={'footerRecentNews'}>Recent News</h5>
                {posts.map((post, index) => <FooterText contentTxt={post} key={index}/>)}
            </div>
            <div className="footerFeed">
            <h5 className={'footerRecentNews'}>Instagram Feed</h5>
            <div className="instagramFeed">
                <div className="instaPost" style={{backgroundImage : image1}}>{images.length}</div>
                <div className="instaPost" style={{backgroundImage : image2}}>{images.length}</div>
                <div className="instaPost" style={{backgroundImage : image3}}>{images.length}</div>
                <div className="instaPost" style={{backgroundImage : image4}}>{images.length}</div>
                <div className="instaPost" style={{backgroundImage : image5}}>{images.length}</div>
                <div className="instaPost" style={{backgroundImage : image6}}>{images.length}</div>
            </div>
            </div>
        </div>
     );
}

export default footer;
