import React from 'react';
import './leftColumn.css';
import MainPost from '../utilities/mainPost/mainPost';

const leftColumn = (props) => {

    const posts = [...props.posts];
    const currentPage = props.currentPage;
    var classPag = 'paginationItem';

    const active = (index) =>  { 
        if((index/3 + 1) === currentPage) {
            classPag = 'paginationItem activePag';
        } else {    
            classPag = 'paginationItem';
        }

    }

    return ( 
        <div className={'leftPart'}>
            <div className={'LeftColumn'}>
            {posts.map((post, index) => (Math.floor(index / 3) + 1) === currentPage ? <MainPost content={post} key={index} /> : null)}
            </div>
            <div className="leftColumnPagination">
                <ul className={'paginationList'} key ={'prev'}>
                    <li className={'paginationItem'} onClick={() => props.onPrev()} >Previous</li>
                    {posts.map((el, index) => index % 3 === 0 ? <li className={classPag} key={el.index} onClick={() => props.onClick(index / 3)} onActive={() => active(index)} >{index/3 + 1}</li> : null )}
                    <li className={'paginationItem'} key={'next'} onClick={() => props.onNext()} >Next</li>
                </ul>
            </div>
        </div>
     );
}

export default leftColumn;
