import React from 'react';
import './subscribtion.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHiking } from '@fortawesome/free-solid-svg-icons'

const subscribtion = (props) => {

    var buttonValue = "CONFIRM";

    const confirmation = () => {
        console.log(props.inputValue);
        let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if ( re.test(props.inputValue) ) {
            // this is a valid email address
            // call setState({email: email}) to update the email
            // or update the data in redux store.
            buttonValue = "THANK YOU !";
            return buttonValue;
        }
        else {
            // invalid email, maybe show an error to the user.
            buttonValue = "TRY AGAIN";
            return buttonValue;
        }

    }

    return ( 
        <div className={'subscribtion'}>
            <FontAwesomeIcon icon={faHiking} className={'fa-5x iconHiking'} style={{marginBottom: 10}} />
            <h4 className={'subTitle'}>JOIN ME ON MY NEXT TRIP</h4>
            <input className={'emailInput'} onChange={props.emailVerification} />
            <button className={'subConfirm'} onClick={confirmation} >{buttonValue}</button>
        </div>
     );
}

export default subscribtion;
