import React from 'react';
import './leftColumn.css';
import MainPost from '../utilities/mainPost/mainPost';

const leftColumn = (props) => {

    const posts = [...props.posts];

    return ( 
        <div claccName={'leftPart'}>
            <div className={'LeftColumn'}>
            {posts.map((post, index) => <MainPost content={post} pageIndex={Math.floor(index / 3)} />)}
            </div>
            <div className="leftColumnPagination">
                <ul class={'paginationList'}>
                    <li className={'paginationItem'}>Previous</li>
                    {posts.map((el, index) => index % 3 === 0 ? <li className={'paginationItem'}>{index/3 + 1}</li> : null )}
                    <li className={'paginationItem'}>Next</li>
                </ul>
            </div>
        </div>
     );
}

export default leftColumn;
