import React from 'react';
import './leftColumn.css';
import MainPost from '../utilities/mainPost/mainPost';

const leftColumn = (props) => {

    const posts = [...props.posts];
    const currentPage = props.currentPage;

    return ( 
        <div className={'leftPart'}>
            <div className={'LeftColumn'}>
            {posts.map((post, index) => Math.floor(index / 3) + 1 === parseInt(currentPage) ? <MainPost content={post} pageIndex={Math.floor(index / 3)} key={index} /> : null)}
            </div>
            <div className="leftColumnPagination">
                <ul className={'paginationList'} key ={'prev'}>
                    <li className={'paginationItem'}>Previous</li>
                    {posts.map((el, index) => index % 3 === 0 ? <li className={'paginationItem'} onClick={(e)=> console.log(this)} key={index} >{index/3 + 1}</li> : null )}
                    <li className={'paginationItem'} key={'next'}>Next</li>
                </ul>
            </div>
        </div>
     );
}

export default leftColumn;
