import React from 'react';
import './footer.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMapMarker } from '@fortawesome/free-solid-svg-icons'
import { faInstagram, faTwitter, faPinterest } from '@fortawesome/free-brands-svg-icons';
import FooterText from '../utilities/footerText/footerText';

const footer = (props) => {

    const posts = [...props.posts].slice(0, 3);

    return ( 
        <div className={'footer'}>
            <div className="footerText">
                <div className="logo footerLogo"><FontAwesomeIcon icon={faMapMarker} /><p>My Next Trip !</p></div><br />
                <p className={'footerP'}>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Tempora, maxime? Quia necessitatibus modi placeat repellendus dolorum tempore voluptatibus? Nisi est.</p>
                <div className="socialLinks footerSocialLinks">
                    <FontAwesomeIcon icon={faInstagram} className={'fa-2x'} style={{margin:'0 15px 0', color:'#628A76'}} />
                    <FontAwesomeIcon icon={faTwitter} className={'fa-2x'} style={{margin:'0 15px 0', color:'#628A76'}} />
                    <FontAwesomeIcon icon={faPinterest} className={'fa-2x'} style={{margin:'0 15px 0', color:'#628A76'}} />
                </div>
            </div>
            <div className="footerNews">
                {posts.map((post, index) => <FooterText content={post} key={index}/>)}
            </div>
            <div className="footerFeed"></div>
        </div>
     );
}

export default footer;
