import './postMoreInfo.css';

const postMoreInfo = (props) => {
    return ( 
        <div className={"postMoreInfo"} style={{display : props.moreInfoDisplay}}>
            <p>More info of : {props.index}</p>
        </div>
     );
}

export default postMoreInfo;
